def CommonSubstrings(S1, S2, limit=4):
    
    M = [[0]*(1+len(S2)) for i in xrange(1+len(S1))]
    matches=[]
    
    S1="".join(S1.lower().splitlines())
    S2="".join(S2.lower().splitlines())

    for x in xrange(1,1+len(S1)):        
        for y in xrange(1,1+len(S2)):
            
            if S1[x-1] == S2[y-1]:
                M[x][y] = M[x-1][y-1] + 1
            else:
                M[x][y] = 0
                if M[x-1][y-1]>limit:                    
                    matches.append((x-1-M[x-1][y-1],y-1-M[x-1][y-1],M[x-1][y-1]))
                    
    #print [M[len(S2)][y] for y in xrange(1,1+len(S2)) if M[len(S2)][y]>limit]
    
            
    return matches

